mapper.select:
select count(*) from dir
where dir_m_id=#{m_id} 
	and dir_name=#{dirName}

mapper.update:
UPDATE dir
SET dir_name=#{dirRename}
WHERE dir_m_id=#{m_id}
	and dir_name=#{dirName}

mapper.delete:
delete from dir
where dir_m_id=#{m_id}
	and dir_name=#{dirName}

mapper.insert:
SELECT dir_num FROM dir where dir_m_id=#{m_id} and dir_name=#{dirName}
insert into image (img_m_id, img_dir_num, img_s_name, img_o_name, img_size, img_thumb)
values(#{m_id}, #{dirNum}, #{imgName}, #{imgOriName}, #{imgLength}, #{imgThumb})

mapper.select:
select img_s_name from image
where img_m_id = #{m_id}
	and img_o_name = #{imgName}
	and img_dir_num=(select dir_num from dir where dir_m_id=#{m_id} and dir_name=#{dirName})

mapper.delete:
delete from image
where img_m_id = #{m_id}
	and img_o_name = #{imgName}
	and img_dir_num=(select dir_num from dir where dir_m_id=#{m_id} and dir_name=#{dirName})
